/*侧边栏开关*/
document.addEventListener('DOMContentLoaded', function() {
	const sidebarMenu = document.getElementById('sidebar-menu');
	const overlay = document.getElementById('overlay');//遮罩层
	const menuBtn = document.querySelector('.oncanvas-mobile-open');
	const closeBtn = document.querySelector('.offcanvas-mobile-close');

	menuBtn.addEventListener('click', function() {
		sidebarMenu.classList.add('active'); // 切换侧边栏显示状态
		overlay.classList.add('active'); // 显示遮罩层
	});

	closeBtn.addEventListener('click', function() {
		sidebarMenu.classList.remove('active'); // 按钮关闭侧边栏
		overlay.classList.remove('active');
	});
	
	overlay.addEventListener('click', function() {
		overlay.classList.remove('active'); // 点击右边空白隐藏遮罩层
		sidebarMenu.classList.remove('active');
	});
});
	
/*侧边栏导航点击展开*/
document.addEventListener('DOMContentLoaded', function() {
	const dropdowns = document.querySelectorAll('.dropdown .menu-item');
	
	dropdowns.forEach(dropdown => {
		dropdown.addEventListener('click', function(event) {
			event.preventDefault();
			const parent = this.parentElement;
			// 关闭所有其他展开的二级菜单
			document.querySelectorAll('.dropdown').forEach(item => {
				if (item !== parent) {
					item.classList.remove('active');
				}
			});

			// 切换当前菜单的展开状态
			parent.classList.toggle('active');
			
			// 获取当前菜单项中的图标，右边“+”号动画
			const icon = this.querySelector('i');
			// 切换图标的类名
			if (icon.classList.contains('fa-plus')) {
				icon.classList.remove('fa-plus');
				icon.classList.add('fa-minus');
			} else {
				icon.classList.remove('fa-minus');
				icon.classList.add('fa-plus');
			}
			// 关闭其他菜单项
			dropdowns.forEach(otherDropdown => {
				if (otherDropdown !== dropdown) {
					otherDropdown.classList.remove('active');
					const otherIcon = otherDropdown.querySelector('.menu-item i');
					otherIcon.classList.remove('fa-minus');
					otherIcon.classList.add('fa-plus');
				}
			});
			
			
			
		});
	});
});


